package Assignment2pattern;

public class Pattern13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=1;i++) {
			for(int j=1;j<=1;j++) {
				System.out.print(" ");
			}for(int j=i;j<=2;j++) {
				if(i==1) {
				System.out.print("*");
				
			}else {
				System.out.print(" ");
			}
				}
			System.out.println(" ");
		}
		for(int i=1;i<=1;i++) {
			for(int j=i;j<=4;j++) {
				if(i==j||j==4)
				System.out.print("*");
				else
					System.out.print(" ");
			}
			System.out.println(" ");
		}
		for(int i=1;i<=1;i++) {
			for(int j=1;j<=1;j++) {
				System.out.print(" ");
			}for(int j=i;j<=2;j++) {
				if(i==1) {
				System.out.print("*");
				
			}else {
				System.out.print(" ");
			}
				}
			System.out.println(" ");
		}
	}

}
